using System;
using NUnit.Framework;
using System.Collections.Generic;
using Platform.Utility.Crytography;

namespace Platform.Utility.Test
{
    [TestFixture]
    public class CryptogahpyTest
    {
        [Test]
        public void TestBase64Encode()
        {
            //Prepare data
            //Invalid length for a Base-64 char array or string.
            string wrongText1 = "";
            string wrongText2 = "abc";
            string wrongText3 = null;

            //Correct text
            //base 64 text

            //Wrong
            Assert.IsNull(Cryptogahpy.Base64Decode(wrongText1));
            Assert.IsNull(Cryptogahpy.Base64Decode(wrongText2));
            Assert.IsNull(Cryptogahpy.Base64Decode(wrongText3));
            
            //Correct
            Assert.AreEqual(true, string.IsNullOrEmpty(Cryptogahpy.Base64Decode(string.Empty)));
        }
    }
}
